package io.deepsearch.domain.agents.googleadkimpl

import io.deepsearch.domain.agents.IIconInterpreterAgent
import io.deepsearch.domain.agents.IconInterpreterInput
import io.deepsearch.domain.config.domainTestModule
import io.deepsearch.domain.constants.ImageMimeType
import kotlinx.coroutines.CoroutineDispatcher
import kotlinx.coroutines.ExperimentalCoroutinesApi
import kotlinx.coroutines.test.runTest
import org.junit.jupiter.api.Test
import org.junit.jupiter.api.assertNotNull
import org.junit.jupiter.api.extension.RegisterExtension
import org.koin.test.KoinTest
import org.koin.test.inject
import org.koin.test.junit5.KoinTestExtension
import kotlin.io.encoding.Base64
import kotlin.io.encoding.ExperimentalEncodingApi
import kotlin.test.assertEquals
import kotlin.test.assertTrue

class IconInterpreterAgentAdkImplTest : KoinTest {

    @JvmField
    @RegisterExtension
    val koinTestExtension = KoinTestExtension.create {
        modules(domainTestModule)
    }

    private val testCoroutineDispatcher by inject<CoroutineDispatcher>()
    private val agent by inject<IIconInterpreterAgent>()

    private val arrowLeftIcon = ""
    private val arrowRightIcon = ""
    private val crossIcon = ""
    private val phoneIcon = ""
    private val searchIcon = ""
    private val doctorIcon = ""
    private val locationIcon = ""
    private val pulseIcon = ""
    private val tickIcon = ""
    private val crossIcon2 = ""
    private val instagramIcon = ""
    private val linkedinIcon = ""
    private val facebookIcon = ""
    private val wechatIcon = ""

    @Test
    fun `icon interpretation returns wechat label for wechat icon`() = runTest(testCoroutineDispatcher) {
        val bytes = Base64.decode(wechatIcon)
        val output = agent.generate(
            IconInterpreterInput(
                bytes = bytes,
                mimeType = ImageMimeType.JPEG
            )
        )
        assertNotNull(output.label, "icon should yield a null label")
        assertTrue(output.label.contains("wechat"), "icon should yield a null label")
    }
}